// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
    return {
      userData: {
        user:{},
        employeeData:{}
      }
    };
  },
  mutations: {
    setUserData(state, { userData, employeeData }) {
      state.userData.user= userData;
      state.userData.employeeData = employeeData;
    },
  },
  actions: {
    setUserData({ commit },{ userData, employeeData }) {
      commit('setUserData',{ userData, employeeData });
    },
  },
  getters: {
    getUserData: (state) => state.userData,
  },
});

export default store;
