// store/index.js
import Vue from 'vue';
import Vuex from 'vuex';

Vue.use(Vuex);

export default new Vuex.Store({
  modules: {
    auth: {
      state: {
        isAuthenticated: false,
      },
      mutations: {
        setAuthenticated(state, isAuthenticated) {
          state.isAuthenticated = isAuthenticated;
        },
      },
      getters: {
        isAuthenticated: state => state.isAuthenticated,
      },
    },
  },
});
