// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
    return {
      userData: null,
    };
  },
  mutations: {
    setUserData(state, { user, employeeData }) {
      state.userData.user = user;
      state.userData.employeeData = employeeData;
    },
  },
  actions: {
    setUserData({ commit },{ user, employeeData }) {
      commit('setUserData',{ user, employeeData } );
    },
  },
  getters: {
    getUserData: (state) => state.userData,
  },
});

export default store;
