// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
    return {
      userData: null,
      team:false,
      responseData:{
        username: '',
      password: ''
      }
    };
  },
  mutations: {
    setUserData(state, userData) {
      state.userData = userData;
    },
    teammembers(state,access){
      state.team=access;
    },
    setresponseData(state,{username, password}){
      state.responseData.username=username;
      state.responseData.password=password;
    }
  },
  actions: {
    setUserData({ commit }, userData) {
      commit('setUserData', userData);
    },
    setresponseData({commit},{username, password}){
      commit('setresponseData',{username, password})
    }
  },
  getters: {
    getUserData: (state) => state.userData,
    getTeamMembers: (state) => state.team,
    getUsername: state => state.userData.username,
    getPassword: state => state.userData.password,


  },
});

export default store;
