// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
   
    const savedLeaveBalances = JSON.parse(localStorage.getItem('leaveBalances'));
    const defaultLeaveBalances = {
      normalLeave: 12,
      sickLeave: 12,
    };
    return {
      userData: null,
      team: false,
      leaveBalances: savedLeaveBalances || defaultLeaveBalances,
    };
  },
  mutations: {
    setUserData(state, userData) {
      state.userData = userData;
    },
    teammembers(state, access) {
      state.team = access;
    },
    updateLeaveBalances(state, { type, days }) {
      if (type === 'Normal Leave') {
        state.leaveBalances.normalLeave -= days;
      } else if (type === 'Sick Leave') {
        state.leaveBalances.sickLeave -= days;
      }
  
      localStorage.setItem('leaveBalances', JSON.stringify(state.leaveBalances));
    },
  },
  actions: {
    setUserData({ commit }, userData) {
      commit('setUserData', userData);
    },
    updateLeaveBalances({ commit }, { type, days }) {
      commit('updateLeaveBalances', { type, days });
    },
  },
  getters: {
    getUserData: (state) => state.userData,
    getTeamMembers: (state) => state.team,
    getLeaveBalances: (state) => state.leaveBalances,
  },
});

export default store;
