// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
    return {
      userData: {
        users: [], // Changed to an array to store multiple users
        employeeData: []
      }
    };
  },
  mutations: {
    setUserData(state, { users, employeeData }) {
      state.userData.users = users;
      state.userData.employeeData = employeeData;
    },
  },
  actions: {
    setUserData({ commit }, { users, employeeData }) {
      commit('setUserData', { users, employeeData });
    },
  },
  getters: {
    getUserData: (state) => state.userData,
  },
});

export default store;
