// store.js
import { createStore } from 'vuex';

const store = createStore({
  state() {
    return {
      userData: null,
      team: false,
      leaveBalances: {}, // Initialize as an empty object
    };
  },
  mutations: {
    setUserData(state, userData) {
      state.userData = userData;
    },
    teammembers(state, access) {
      state.team = access;
    },
    initializeLeaveBalances(state) {
      // Check if leave balances exist in localStorage for the current user
      const userLeaveBalances = JSON.parse(localStorage.getItem(`leaveBalances_${state.userData.id}`));
      state.leaveBalances = userLeaveBalances || { normalLeave: 12, sickLeave: 12 }; // Use default values if not found
    },
    updateLeaveBalances(state, { type, days }) {
      if (type === 'Normal Leave') {
        state.leaveBalances.normalLeave -= days;
      } else if (type === 'Sick Leave') {
        state.leaveBalances.sickLeave -= days;
      }
      // Save updated leave balances to localStorage for the current user
      localStorage.setItem(`leaveBalances_${state.userData.id}`, JSON.stringify(state.leaveBalances));
    },
  },
  actions: {
    setUserData({ commit }, userData) {
      commit('setUserData', userData);
      commit('initializeLeaveBalances');
    },
    updateLeaveBalances({ commit }, { type, days }) {
      commit('updateLeaveBalances', { type, days });
    },
  },
  getters: {
    getUserData: (state) => state.userData,
    getTeamMembers: (state) => state.team,
    getLeaveBalances: (state) => state.leaveBalances,
  },
});

export default store;
